						$message .= "$property"."->ugly, ";
						break;
					case 'risky':
						$state = max($state, 1);
						$message .= "$property"."->risky, ";
						break;
					case 'bad':
						$state = max($state, 2);
						$message .= "$property"."->BAD, ";
						break;
					case 'info':
						$state = max($state, 3);
						$message .= "$property"."->info, ";
						break;
					case 'good':
					case 'safe':
						break;
				}
			}
		}
		$monitoring_info['state'] = max($monitoring_info['state'], $state);
		if ($state != 0) {
			$monitoring_info['message'] .= $check_group_name.": ".trim($message, ' ,')." -- ";
